mapper.select:
SELECT tbboard.id ,tbboard.title ,tbboard.content ,tbboard.deleted ,tbboard.created_at ,tbboard.modified_at FROM tbboard WHERE tbboard.id is not null AND tbboard.created_at >= CONCAT(#{sdate}, ' 00:00:00.000000') AND CONCAT(#{fdate}, ' 23:59:59.999999') >= tbboard.created_at AND tbboard.title LIKE CONCAT('%', #{title}, '%') AND tbboard.deleted = #{deleted}
AND STR_TO_DATE(#{cdatetime}, '%Y-%m-%d %H:%i:%s.%f') > tbboard.created_at ORDER BY tbboard.created_at DESC AND tbboard.created_at > STR_TO_DATE(#{cdatetime}, '%Y-%m-%d %H:%i:%s.%f') ORDER BY tbboard.created_at ASC
LIMIT 0, #{permore}
