mapper.select:
SELECT tbcmt.id ,tbcmt.tbboard_id ,tbcmt.content ,tbcmt.deleted ,tbcmt.created_at ,tbcmt.modified_at FROM tbcmt WHERE tbcmt.id is not null AND tbcmt.created_at >= CONCAT(#{sdate}, ' 00:00:00.000000') AND CONCAT(#{fdate}, ' 23:59:59.999999') >= tbcmt.created_at AND tbcmt.tbboard_id = #{tbboard_id} AND tbcmt.content LIKE CONCAT('%', #{content}, '%') AND tbcmt.deleted = #{deleted}
AND STR_TO_DATE(#{cdatetime}, '%Y-%m-%d %H:%i:%s.%f') > tbcmt.created_at ORDER BY tbcmt.created_at DESC AND tbcmt.created_at > STR_TO_DATE(#{cdatetime}, '%Y-%m-%d %H:%i:%s.%f') ORDER BY tbcmt.created_at ASC
LIMIT 0, #{permore}
