SELECT tbcrewushot.id
FROM tbcrewushot, tbcrewuser
WHERE tbcrewushot.tbcrewuser_id = tbcrewuser.id
AND tbcrewushot.deleted = #{deleted}
AND tbcrewushot.tbcrewuser_id = #{tbcrewuserId}
AND tbcrewushot.level = #{level}
AND tbcrewushot.content = #{content}
AND tbcrewuser.tbcrew_id = #{tbcrewId}
ORDER BY tbcrewushot.created_at ASC

SELECT tbcrewushot.id
FROM tbcrewushot
WHERE tbcrewushot.id is not null
AND tbcrewushot.deleted = #{deleted}
AND tbcrewuser.tbcrew_id = #{tbcrewId}
AND tbcrewushot.tbcrewuser_id = #{tbcrewuserId}
AND tbcrewushot.level = #{level}
AND tbcrewushot.content = #{content}
AND tbcrewushot.created_at >= CONCAT(#{sdate}, ' 00:00:00.000000')
AND CONCAT(#{fdate}, ' 23:59:59.999999') >= tbcrewushot.created_at

	AND STR_TO_DATE(#{cdatetime}, '%Y-%m-%d %H:%i:%s.%f') > tbcrewushot.created_at ORDER BY tbcrewushot.created_at DESC
	AND tbcrewushot.created_at > STR_TO_DATE(#{cdatetime}, '%Y-%m-%d %H:%i:%s.%f') ORDER BY tbcrewushot.created_at ASC

LIMIT 0, #{permore}

SELECT tbcrewushot.id
FROM tbcrewushot
WHERE tbcrewushot.id is not null
AND tbcrewushot.deleted = #{deleted}
AND tbcrewuser.tbcrew_id = #{tbcrewId}
AND tbcrewushot.tbcrewuser_id = #{tbcrewuserId}
AND tbcrewushot.level = #{level}
AND tbcrewushot.content = #{content}
AND tbcrewushot.created_at >= CONCAT(#{sdate}, ' 00:00:00.000000')
AND CONCAT(#{fdate}, ' 23:59:59.999999') >= tbcrewushot.created_at
ORDER BY tbcrewushot.created_at 
ASC
DESC
LIMIT #{callpage}, #{perpage}

SELECT count(tbcrewushot.id) as listsize
FROM tbcrewushot
WHERE tbcrewushot.id is not null
AND tbcrewushot.deleted = #{deleted}
AND tbcrewuser.tbcrew_id = #{tbcrewId}
AND tbcrewushot.tbcrewuser_id = #{tbcrewuserId}
AND tbcrewuser.level = #{level}
AND tbcrewuser.content = #{content}
AND tbcrewushot.created_at >= CONCAT(#{sdate}, ' 00:00:00.000000')
AND CONCAT(#{fdate}, ' 23:59:59.999999') >= tbcrewushot.created_at
AND tbcrewushot.deleted = #{deleted}
