mapper.select:
SELECT tbgrantuser.id
FROM tbgrantuser
WHERE tbgrantuser.id is not null
AND tbgrantuser.tbgrant_id = #{tbgrantId}
AND tbgrantuser.tbuser_id = #{tbuserId}
AND tbgrantuser.content LIKE CONCAT('%', #{content}, '%')
AND tbgrantuser.deleted = #{deleted}
ORDER BY tbgrantuser.created_at ASC

SELECT tbgrantuser.id
FROM tbgrantuser
WHERE tbgrantuser.id is not null
AND tbgrantuser.created_at >= CONCAT(#{sdate}, ' 00:00:00.000000')
AND CONCAT(#{fdate}, ' 23:59:59.999999') >= tbgrantuser.created_at
AND tbgrantuser.tbgrant_id = #{tbgrantId}
AND tbgrantuser.tbuser_id = #{tbuserId}
AND tbgrantuser.content LIKE CONCAT('%', #{content}, '%')
AND tbgrantuser.deleted = #{deleted}

	AND STR_TO_DATE(#{cdatetime}, '%Y-%m-%d %H:%i:%s.%f') > tbgrantuser.created_at ORDER BY tbgrantuser.created_at DESC
	AND tbgrantuser.created_at > STR_TO_DATE(#{cdatetime}, '%Y-%m-%d %H:%i:%s.%f') ORDER BY tbgrantuser.created_at ASC

LIMIT 0, #{permore}

SELECT tbgrantuser.id
FROM tbgrantuser
WHERE tbgrantuser.id is not null
AND tbgrantuser.created_at >= CONCAT(#{sdate}, ' 00:00:00.000000')
AND CONCAT(#{fdate}, ' 23:59:59.999999') >= tbgrantuser.created_at
AND tbgrantuser.tbgrant_id = #{tbgrantId}
AND tbgrantuser.tbuser_id = #{tbuserId}
AND tbgrantuser.content LIKE CONCAT('%', #{content}, '%')
AND tbgrantuser.deleted = #{deleted}
ORDER BY tbgrantuser.created_at 
ASC
DESC
LIMIT #{callpage}, #{perpage}

SELECT count(tbgrantuser.id) as listsize
FROM tbgrantuser
WHERE tbgrantuser.id is not null
AND tbgrantuser.created_at >= CONCAT(#{sdate}, ' 00:00:00.000000')
AND CONCAT(#{fdate}, ' 23:59:59.999999') >= tbgrantuser.created_at
AND tbgrantuser.tbgrant_id = #{tbgrantId}
AND tbgrantuser.tbuser_id = #{tbuserId}
AND tbgrantuser.content LIKE CONCAT('%', #{content}, '%')
AND tbgrantuser.deleted = #{deleted}
