mapper.select:
SELECT tbpic.id ,tbpic.tbboard_id ,tbpic.content ,tbpic.deleted ,tbpic.created_at ,tbpic.modified_at FROM tbpic WHERE tbpic.id is not null AND tbpic.created_at >= CONCAT(#{sdate}, ' 00:00:00.000000') AND CONCAT(#{fdate}, ' 23:59:59.999999') >= tbpic.created_at AND tbpic.tbboard_id = #{tbboard_id} AND tbpic.content LIKE CONCAT('%', #{content}, '%') AND tbpic.type = #{type} AND tbpic.deleted = #{deleted}
AND STR_TO_DATE(#{cdatetime}, '%Y-%m-%d %H:%i:%s.%f') > tbpic.created_at ORDER BY tbpic.created_at DESC AND tbpic.created_at > STR_TO_DATE(#{cdatetime}, '%Y-%m-%d %H:%i:%s.%f') ORDER BY tbpic.created_at ASC
LIMIT 0, #{permore}

SELECT count(tbpic.id) as listsize
FROM tbpic
WHERE tbpic.id is not null
AND tbpic.created_at >= CONCAT(#{sdate}, ' 00:00:00.000000')
AND CONCAT(#{fdate}, ' 23:59:59.999999') >= tbpic.created_at
AND tbpic.tbboard_id = #{tbboard_id}
AND tbpic.content LIKE CONCAT('%', #{content}, '%')
AND tbpic.type = #{type}
AND tbpic.deleted = #{deleted}
