mapper.select:
SELECT tbpost.id
FROM tbpost
WHERE tbpost.id is not null
AND tbpost.deleted = #{deleted}
AND tbpost.title LIKE CONCAT('%', #{title}, '%')
AND tbpost.cate = #{cate}

SELECT tbpost.id
FROM tbpost, tbuser
WHERE tbpost.tbuser_id = tbuser.id
AND tbpost.deleted = #{deleted}
AND tbpost.title LIKE CONCAT('%', #{title}, '%')
AND tbpost.cate = #{cate}
AND tbpost.tbuser_id = #{tbuserId}
AND tbuser.username = #{tbuserUsername}
AND tbuser.nick LIKE CONCAT('%', #{tbuserNick}, '%')
AND tbpost.created_at >= CONCAT(#{sdate}, ' 00:00:00.000000')
AND CONCAT(#{fdate}, ' 23:59:59.999999') >= tbpost.created_at

	AND STR_TO_DATE(#{cdatetime}, '%Y-%m-%d %H:%i:%s.%f') > tbpost.created_at ORDER BY tbpost.created_at DESC
	AND tbpost.created_at > STR_TO_DATE(#{cdatetime}, '%Y-%m-%d %H:%i:%s.%f') ORDER BY tbpost.created_at ASC

LIMIT 0, #{permore}

SELECT tbpost.id
FROM tbpost, tbuser
WHERE tbpost.tbuser_id = tbuser.id
AND tbpost.deleted = #{deleted}
AND tbpost.title LIKE CONCAT('%', #{title}, '%')
AND tbpost.cate = #{cate}
AND tbpost.tbuser_id = #{tbuserId}
AND tbuser.username = #{tbuserUsername}
AND tbuser.nick LIKE CONCAT('%', #{tbuserNick}, '%')
AND tbpost.created_at >= CONCAT(#{sdate}, ' 00:00:00.000000')
AND CONCAT(#{fdate}, ' 23:59:59.999999') >= tbpost.created_at
ORDER BY tbpost.created_at 
ORDER BY tbpost.title 
ASC
DESC
LIMIT #{callpage}, #{perpage}

SELECT count(tbpost.id) as listsize
FROM tbpost, tbuser
WHERE tbpost.tbuser_id = tbuser.id
AND tbpost.deleted = #{deleted}
AND tbpost.title LIKE CONCAT('%', #{title}, '%')
AND tbpost.cate = #{cate}
AND tbpost.tbuser_id = #{tbuserId}
AND tbuser.username = #{tbuserUsername}
AND tbuser.nick LIKE CONCAT('%', #{tbuserNick}, '%')
AND tbpost.created_at >= CONCAT(#{sdate}, ' 00:00:00.000000')
AND CONCAT(#{fdate}, ' 23:59:59.999999') >= tbpost.created_at
