mapper.select:
SELECT tbpostcmt.id
FROM tbpostcmt
WHERE tbpostcmt.id is not null
AND tbpostcmt.tbpost_id = #{tbpostId}
AND tbpostcmt.tbuser_id = #{tbuserId}
AND tbpostcmt.content LIKE CONCAT('%', #{content}, '%')
AND tbpostcmt.deleted = #{deleted}
ORDER BY tbpostcmt.created_at ASC

SELECT tbpostcmt.id
FROM tbpostcmt
WHERE tbpostcmt.id is not null
AND tbpostcmt.created_at >= CONCAT(#{sdate}, ' 00:00:00.000000')
AND CONCAT(#{fdate}, ' 23:59:59.999999') >= tbpostcmt.created_at
AND tbpostcmt.tbpost_id = #{tbpostId}
AND tbpostcmt.tbuser_id = #{tbuserId}
AND tbpostcmt.content LIKE CONCAT('%', #{content}, '%')
AND tbpostcmt.deleted = #{deleted}

	AND STR_TO_DATE(#{cdatetime}, '%Y-%m-%d %H:%i:%s.%f') > tbpostcmt.created_at ORDER BY tbpostcmt.created_at DESC
	AND tbpostcmt.created_at > STR_TO_DATE(#{cdatetime}, '%Y-%m-%d %H:%i:%s.%f') ORDER BY tbpostcmt.created_at ASC

LIMIT 0, #{permore}

SELECT tbpostcmt.id
FROM tbpostcmt
WHERE tbpostcmt.id is not null
AND tbpostcmt.created_at >= CONCAT(#{sdate}, ' 00:00:00.000000')
AND CONCAT(#{fdate}, ' 23:59:59.999999') >= tbpostcmt.created_at
AND tbpostcmt.tbpost_id = #{tbpostId}
AND tbpostcmt.tbuser_id = #{tbuserId}
AND tbpostcmt.content LIKE CONCAT('%', #{content}, '%')
AND tbpostcmt.deleted = #{deleted}
ORDER BY tbpostcmt.created_at 
ASC
DESC
LIMIT #{callpage}, #{perpage}
